import './App.css';
import {useState} from "react";

const App = () => {

  const [word, setWord] = useState()

  const computeDisplay = (word, usedLetters) => word.replace(/\w/g,(letter) => (usedLetters.has(letter) ? letter : '_'));

  return (
    <div className="App">
      <header className="App-header">
        <p>
          Edit <code>src/App.js</code> and save to reload.
        </p>
        <a
          className="App-link"
          href="https://reactjs.org"
          target="_blank"
          rel="noopener noreferrer"
        >
          Learn React
        </a>
      </header>
    </div>
  );
}

export default App;
