import {useState} from "react";

const App = () => {

  const [word, setWord] = useState("testing");
  const [usedLetters, setUsedLetters] = useState([]);

  const computeDisplay = (word, usedLetters) => word.replace(/\w/g,(letter) => (usedLetters.include(letter) ? letter : '_'));

  return (
    <div className="App">
      <h1>{computeDisplay(word, usedLetters)}</h1>
    </div>
  );
}

export default App;
