import {useState} from 'react'

const Keyboard = ({ line1 }) => {
    const line2 = line1.splice(0, 13);
    return (
        <div>
            <div>{ line1.map((letter, index) => <button key={index}>{letter}</button>) }</div>
            <div>{ line2.map((letter, index) => <button key={index}>{letter}</button>) }</div>
        </div>
    );
}

export default Keyboard
